--  DDL for View INVOICE_REPORT
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "TRAVEL"."INVOICE_REPORT" ("ID", "INVOICE_ORDER", "TRANSACTION_ID", "INVOICE_NUMBER", "BOOKING_FILE_NUMBER", "DEPARTURE_DATE", "ARRIVAL_DATE", "EMPLOYEE_ID", "COST_CENTER", "COST_CENTER_DEPARTMENT", "PASSENGER_NAME", "SERVICE_TYPE", "SERVICE_DESC", "ROUTING", "INTER_DOM", "CHECK_IN", "CHECK_OUT", "NUMBER_OF_NIGHTS", "NUMBER_OF_ROOMS", "AIRLINE", "ROOM_TYPE", "SUPPLIER_NAME", "NET_AMOUNT", "OPERATION_FEES", "TOTAL_AMOUNT", "TICKET_NO", "TRAVEL_FORM_NUMBER", "DESCRIPTION", "FROM_DATE", "TO_DATE", "UPLOADED_INVOICE_FILE_ID", "EMPLOYEE_DEPARTMENT", "INVOICE_DATE", "ACTION_TAKEN", "ACTION_TAKEN_COMMENT", "INVOICES_MONTH", "NAME") AS 
  SELECT INVOICES."ID",
    INVOICES."INVOICE_ORDER",
    INVOICES."TRANSACTION_ID",
    INVOICES."INVOICE_NUMBER",
    INVOICES."BOOKING_FILE_NUMBER",
    INVOICES."DEPARTURE_DATE",
    INVOICES."ARRIVAL_DATE",
    INVOICES."EMPLOYEE_ID",
    INVOICES."COST_CENTER",
    INVOICES."COST_CENTER_DEPARTMENT",
    INVOICES."PASSENGER_NAME",
    INVOICES."SERVICE_TYPE",
    INVOICES."SERVICE_DESC",
    INVOICES."ROUTING",
    INVOICES."INTER_DOM",
    INVOICES."CHECK_IN",
    INVOICES."CHECK_OUT",
    INVOICES."NUMBER_OF_NIGHTS",
    INVOICES."NUMBER_OF_ROOMS",
    INVOICES."AIRLINE",
    INVOICES."ROOM_TYPE",
    INVOICES."SUPPLIER_NAME",
    INVOICES."NET_AMOUNT",
    INVOICES."OPERATION_FEES",
    INVOICES."TOTAL_AMOUNT",
    INVOICES."TICKET_NO",
    INVOICES."TRAVEL_FORM_NUMBER",
    INVOICES."DESCRIPTION",
    INVOICES."FROM_DATE",
    INVOICES."TO_DATE",
    INVOICES."UPLOADED_INVOICE_FILE_ID",
    INVOICES."EMPLOYEE_DEPARTMENT",
    INVOICES."INVOICE_DATE",
    CASE 
     WHEN UPLOADED_INVOICE_FILE.APPROVED IS NULL THEN 'No action Taken'
     WHEN UPLOADED_INVOICE_FILE.APPROVED  =0 THEN 'Rejected'
     WHEN UPLOADED_INVOICE_FILE.APPROVED  =1 THEN 'Approved'
    END AS ACTION_TAKEN,
    UPLOADED_INVOICE_FILE.REJECTION_REASON AS ACTION_TAKEN_COMMENT,
    UPLOADED_INVOICE_FILE.INVOICES_MONTH,
    UPLOADED_INVOICE_FILE.NAME
    FROM  UPLOADED_INVOICE_FILE INNER
  JOIN INVOICES ON UPLOADED_INVOICE_FILE.ID = INVOICES.UPLOADED_INVOICE_FILE_ID;
