--  DDL for View RATES_VIEW
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "TRAVEL"."RATES_VIEW" ("SERVICE_NAME", "PRODUCT_NAME", "SUPPLIER_NAME", "AIRLINE_NAME", "ROUTING", "RATE", "ROOM_TYPE_NAME", "YEAR", "SUPPLIER_PRODUCT_ID", "ROOM_TYPE_ID", "AIRLINE_ID", "SERVICE_ID", "PRODUCT_ID", "SUPPLIER_ID") AS 
  SELECT ST.NAME            AS SERVICE_NAME,
  PT.NAME                 AS PRODUCT_NAME,
  S.NAME                  AS SUPPLIER_NAME,
  NVL(AL.NAME, 'NA')      AS AIRLINE_NAME,
  NVL(R.ROUTING, 'NA')    AS ROUTING,
  R.RATE                  AS RATE,
  NVL(RT.NAME, 'NA')      AS ROOM_TYPE_NAME,
  R.YEAR                  AS YEAR,
  R.SUPPLIER_PRODUCT_ID   AS SUPPLIER_PRODUCT_ID,
  NVL(R.ROOM_TYPE_ID, -1) AS ROOM_TYPE_ID,
  NVL(R.AIRLINE_ID,   -1) AS AIRLINE_ID,
  ST.ID                   AS SERVICE_ID,
  PT.ID                   AS PRODUCT_ID,
  S.ID                    AS SUPPLIER_ID
FROM SERVICE_TYPE ST
INNER JOIN PRODUCT_TYPE PT
ON ST.ID = PT.SERVICE_ID
INNER JOIN SUPPLIER_PRODUCT SP
ON PT.ID = SP.PRODUCT_ID
INNER JOIN SUPPLIER S
ON S.ID = SP.SUPPLIER_ID
INNER JOIN RATES R
ON SP.ID = R.SUPPLIER_PRODUCT_ID
LEFT JOIN AIRLINE AL
ON AL.ID = R.AIRLINE_ID
LEFT JOIN ROOM_TYPE RT
ON RT.ID = R.ROOM_TYPE_ID;
